import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Card from './components/Card.js';
import cssApp from './App.css';



const App = () => {

    const [data, setData] = useState([]);
    const [research, setResearch] = useState('');

    useEffect(() => {
        axios.get("https://www.themealdb.com/api/json/v1/1/search.php?s=beef").then((res) => setData(res.data.meals));
    }, [research])
    //console.log(data);
    console.log(research.value);

    return (

        <div className='App'>
            <h1>Recettes de cuisine</h1>
            <input
                type="text"
                placeholder='Search'
                className='search-bar'
                onChange={(event) => setResearch(event.target.value)}
                value={research} />


            <div className='dish-cards'>
                {data.map((plat) =>
                    <Card
                        key={plat.idMeal}
                        dish={plat}
                        name={plat.strMeal}
                        origin={plat.strArea}
                        picture={plat.strMealThumb}
                        description={plat.strInstructions}>
                    </Card>
                )}
            </div>


        </div >
    );
};

export default App;
