import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Article from './Article.js';
import Menu from './Menu.js';


const Blog = () => {

    const [data, setData] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:3003/articles").then((res) => setData(res.data));
    }, []);


    const addArticle = (element) => {
        element.preventDefault();
    }

    return (
        <div id='Blog'>
            <Menu></Menu>

            <h1>Blog</h1>

            <form id='div-input-article' onSubmit={element}>
                <input
                    type="text"
                    placeholder='Nom'
                    className='name-input'
                    onChange={element} />

                <input
                    type="text"
                    placeholder='Message'
                    className='message-input' />
            </form>

            <div id='div-output-article'>
                {data.map((element) =>
                    <Article author={element.author} date={element.date} message={element.content} />
                )}
            </div>
        </div>
    );
};

export default Blog;
