import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Card from './components/Card.js';



const App = () => {

    const [data, setData] = useState([]);

    useEffect(() => {
        axios.get("https://www.themealdb.com/api/json/v1/1/search.php?s=beef").then((res) => setData(res.data.meals));
    }, [])
    //console.log(data);


    return (

        <div className='App'>

            {data.map((plat) =>
                <Card
                    key={plat.idMeal}
                    dish={plat}
                    name={plat.strMeal}
                    origin={plat.strArea}
                    picture={plat.strMealThumb}
                    description={plat.strInstructions}>
                </Card>
            )}

        </div >
    );
};

export default App;
